class CategoriaNormativa extends BaseCategoriaNormativa
{
	public function __toString()
	{
		return $this->nombre;
	}
	
	public static function getRepository()
	{
		return Doctrine::getTable(__CLASS__);
	}

	public static function getArrayCategoria()
	{
		$categoria = CategoriaNormativaTable::getAll();
		
		$arraycategoria = array('0'=>'--seleccionar--');
		
		foreach ($categoria as $c)
		{
			
			$arraycategoria[$c->getId()] = $c->getNombre();
			
		}
		
		return $arraycategoria;
	}
		
}